mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from
    (
        select * from board 
        
        order by board_no desc
    )A
)

where RNUM>#{firstRecordIndex} 
	and RNUM<=#{firstRecordIndex}+ #{recordCountPerPage}

mapper.update:
update board
set name=#{name},title=#{title}, email=#{email}, content=#{content},file_size=#{fileSize}
,file_name=#{fileName}, original_file_Name=#{originalFileName}
where Board_No=#{boardNo}

update board
set title=#{title}, content=#{content}

	, file_name=#{fileName}, file_size=#{fileSize},
	original_file_name=#{originalFileName, jdbcType=VARCHAR}
		
where board_no=#{boardNo}
